import React, { useContext } from 'react';
import './Cube.scss';
import { GlobalContext } from 'context/GlobalContext';

const Cube = () => {
  const { xVal, yVal, zVal, aVal, borderActive, shineActive } = useContext(
    GlobalContext
  );

  const dynamicStyle = {
    border: borderActive && '2px rgb(255, 255, 255) solid',
    boxShadow: shineActive && '0.5rem 0.5rem 20rem rgba(255, 255, 255, 0.14)'
  };
  return (
    <div className='scene'>
      <div
        className='cube '
        style={{
          transform: `rotate3d(${xVal}, ${yVal}, ${zVal}, ${aVal}deg)`
        }}
      >
        <div
          style={{
            border: borderActive ? '2px rgb(255, 255, 255) solid' : 'none'
          }}
          className='side front'
        >
          1
        </div>
        <div
          style={{
            border: borderActive ? '2px rgb(255, 255, 255) solid' : 'none'
          }}
          className='side back'
        >
          6
        </div>
        <div
          style={{
            border: borderActive ? '2px rgb(255, 255, 255) solid' : 'none'
          }}
          className='side right'
        >
          3
        </div>
        <div
          style={{
            border: borderActive ? '2px rgb(255, 255, 255) solid' : 'none'
          }}
          className='side left'
        >
          4
        </div>
        <div
          style={{
            border: borderActive ? '2px rgb(255, 255, 255) solid' : 'none'
          }}
          className='side top'
        >
          2
        </div>
        <div
          style={{
            border: borderActive && '2px rgb(255, 255, 255) solid',
            boxShadow:
              shineActive && '0.5rem 0.5rem 20rem rgba(255, 255, 255, 0.14)'
          }}
          className='side bottom'
        >
          5
        </div>
      </div>
    </div>
  );
};

export default Cube;
