import React, { useContext } from 'react';

import { GlobalContext } from 'context/GlobalContext';

const CheckBoxes = () => {
  const { bordersActive, toggleBorders } = useContext(GlobalContext);
  console.log('bordersActive: ', bordersActive);
  return (
    <div>
      <input
        checked={bordersActive}
        onChange={e => {
          toggleBorders(e.target.checked);
        }}
        type='checkbox'
      ></input>{' '}
      borders
    </div>
  );
};

export default CheckBoxes;
